import React from "react";
import Image from "./image";
import Name from "./name"
import Detail from "./detail";
function card(props) {
  return (
    <div>
      <div className="card">
        <div className="top">
        <h2 className="name">{props.name}</h2>
          <Image img= {props.img}/>
          
        </div>
        <div className="bottom">
          <Detail detailif ={props.phone}/>
          <Detail detailif ={props.email}/>
      
        </div>
      </div>
    </div>
  );
}

export default card;
