import React from "react";
import Image from "./image";
import Name from "./name"
function card(props) {
  return (
    <div>
      <div className="card">
        <div className="top">
           {props.name}
          <Image img= {props.img}/>
          
        </div>
        <div className="bottom">
          <p className="info">{props.phone}</p>
          <p className="info">{props.email}</p>
        </div>
      </div>
    </div>
  );
}

export default card;
